from celery import Celery
from os import system


app = Celery('tasker',
            broker="pyamqp://guest@cyberhead-rmq//")


@app.task
def see_you():
    print("See you in ten seconds!")
    #system(python3 /app/cyberhead/web-gui/web-gui.py)


app.conf.beat_schedule = {
    "see-you": {
        "task": "periodic.see_you",
        "schedule": 5.0
    }
}


app.run()
